function colorMe(){var o=randomIntFromInterval(1,3);switch(o){case 1:document.body.style.backgroundColor="#0099ff";break;case 2:document.body.style.backgroundColor="#ff6600";break;case 3:document.body.style.backgroundColor="#666699"}}function randomIntFromInterval(o,r){return Math.floor(Math.random()*(r-o+1)+o)}colorMe();